import './App.css';
import Calender from './components/Calender/Calender.js'
/*import Appointment from './components/Appointment.js'*/
import { Todo } from './components/ToDo.js'
import UpcomingEvents from './components/UpcomingEvents.js'
import API1 from './components/API1.js'
import API2 from './components/API2.js'
import API3 from './components/API3.js'
import React, { useState } from 'react';

function App() {

  const [appointments, setAppointments] = useState(
    JSON.parse(localStorage.getItem('appointments')) || {}
  );

  const handleSaveAppointment = (updatedAppointments) => {
    setAppointments(updatedAppointments);
  };

  return (
    <div bg-auto style={{ backgroundImage: "url(${Background})" }}>
      <div className="grid grid-rows-3 grid-cols-1 gap-4 text-pink-500 sm:grid-cols-3 sm:grid-rows-6">

        <div className="row-span-1 col-span-1 sm:col-span-3 sm:col-start-2">
          <UpcomingEvents appointments={appointments} />
        </div>
        <div className="row-span-2 col-span-1 sm:row-span-1 sm:col-span-3">
          <Calender />
        </div>
        <div className="row-span-1 col-span-1 sm:row-span-1 sm:col-span-3">
          <Todo />
        </div>

        <div className="row-span-1 col-span-1 pt-5 sm:row-span-1 sm:col-span-1">
          <API1 />
        </div>
        <div className="row-span-1 col-span-1 pt-5 sm:row-span-1 sm:col-span-1">
          <API2 />
        </div>
        <div className="row-span-1 col-span-1 pt-5 sm:row-span-1 sm:col-span-1">
          <API3 />
        </div>

      </div>
    </div>
  );
}

export default App;
