import React, { useState, useEffect } from 'react';

function UpcomingEvents() {
  const [appointments, setAppointments] = useState({});

  useEffect(() => {
    // Fetch appointments from localStorage and set the state
    const savedAppointments = JSON.parse(localStorage.getItem('appointments')) || {};
    setAppointments(savedAppointments);
  }, []);

  // Function to sort appointments by date
  const sortAppointmentsByDate = (appointments) => {
    return Object.entries(appointments).sort(([dateStringA], [dateStringB]) => {
      const dateA = new Date(dateStringA);
      const dateB = new Date(dateStringB);
      return dateA - dateB;
    });
  };

  // Sort the appointments by date before rendering
  const sortedAppointments = sortAppointmentsByDate(appointments);

  return (
    <div className="upcoming-events">
      <h2>Upcoming Events</h2>
      <ul>
        {sortedAppointments.map(([dateString, appointment], index) => (
          <li key={index}>
            {new Date(dateString).toDateString()}: {appointment}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UpcomingEvents;
