import React, { useState } from 'react';
import Calendar from './components/Calendar/Calender.js'; // Corrected the import name
import UpcomingEvents from './components/UpcomingEvents';
import AppointmentPopup from './components/Popup/AppointmentPopup';

function App() {
  const [appointments, setAppointments] = useState(
    JSON.parse(localStorage.getItem('appointments')) || {}
  );
  const [selectedDate, setSelectedDate] = useState(null);
  const [showPopup, setShowPopup] = useState(false);

  const handleDayClick = (day) => {
    setSelectedDate(day);
    setShowPopup(true);
  };

  const handleSaveAppointment = (date, appointment) => {
    const dateString = date.toDateString();
    setAppointments((prevAppointments) => ({
      ...prevAppointments,
      [dateString]: appointment,
    }));
    localStorage.setItem(
      'appointments',
      JSON.stringify({ ...appointments, [dateString]: appointment })
    );
    setShowPopup(false);
  };

  const handleDeleteAppointment = (dateString) => {
    const updatedAppointments = { ...appointments };
    delete updatedAppointments[dateString];
    setAppointments(updatedAppointments);
    localStorage.setItem('appointments', JSON.stringify(updatedAppointments));
  };

  return (
    <div>
      <div className="grid grid-rows-3 grid-cols-3 gap-4 text-pink-400">
        <div className="row-span-1 col-span-1">
          {/* Make sure the 'appointments' prop is passed here */}
          <UpcomingEvents appointments={appointments} onDeleteAppointment={handleDeleteAppointment} />
        </div>
        <div className="row-span-2 col-span-2">
          <Calendar
            onDayClick={handleDayClick}
            appointments={appointments}
            selectedDate={selectedDate}
            onSave={handleSaveAppointment}
          />
        </div>
      </div>
      {showPopup && (
        <AppointmentPopup
          selectedDate={selectedDate}
          onSave={handleSaveAppointment}
          onClose={() => setShowPopup(false)}
        />
      )}
    </div>
  );
}

export default App;
