import React, { useEffect, useState } from 'react';

function Days({ currentMonth, onDayClick }) {
  // ... (your existing code)

  // Define a state to store the appointments
  const [appointments, setAppointments] = useState({});

  // Load appointments from localStorage on component mount
  useEffect(() => {
    const savedAppointments = JSON.parse(localStorage.getItem('appointments')) || {};
    setAppointments(savedAppointments);
  }, []);

  const hasAppointment = (date) => {
    const dateString = date.toDateString();
    return appointments.hasOwnProperty(dateString);
  };

  // ... (your existing code)

  const handleSquareClick = (date) => {
    const dateString = date.toDateString();
    if (appointments[dateString]) {
      alert(`Appointment for ${dateString}: ${appointments[dateString]}`);
    } else {
      alert(`No appointment for ${dateString}`);
    }
  };

  // ... (your existing code)

  return (
    <tbody className="bg-transparent">
      <tr>
        <td className="bg-fuchsia-600 border-2 text-s p-1">Monday</td>
        <td className="bg-fuchsia-600 border-2 text-s p-1">Tuesday</td>
        <td className="bg-fuchsia-600 border-2 text-s p-1">Wednesday</td>
        <td className="bg-fuchsia-600 border-2 text-s p-1">Thursday</td>
        <td className="bg-fuchsia-600 border-2 text-s p-1">Friday</td>
        <td className="bg-fuchsia-600 border-2 text-s p-1">Saturday</td>
        <td className="bg-fuchsia-600 border-2 text-s p-1">Sunday</td>
      </tr>
      {fillCalendar().map((row, index) => (
        <tr key={`row-${index}`}>
          {row.map((cell, idx) => (
            <td key={`cell-${index}-${idx}`}>
              {cell}
              {cell.props.className.includes('bg-pink-400') && (
                <span
                  className="w-2 h-2 bg-pink-400 inline-block cursor-pointer"
                  onClick={() => handleSquareClick(new Date(currentMonth.getFullYear(), currentMonth.getMonth(), cell.props.children))}
                />
              )}
            </td>
          ))}
        </tr>
      ))}
    </tbody>
  );
}

export default Days;
