// Calender.js
import React, { useState } from 'react';
import Days from './Days';
import AppointmentPopup from '../Popup/AppointmentPopup';

function Calender() {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [appointments, setAppointments] = useState(
    JSON.parse(localStorage.getItem('appointments')) || {}
  );

  const handleDayClick = (day) => {
    setSelectedDate(day);
    setShowPopup(true);
  };

  const handleSaveAppointment = (date, appointment) => {
    const dateString = date.toDateString();
    setAppointments((prevAppointments) => ({
      ...prevAppointments,
      [dateString]: appointment,
    }));
    localStorage.setItem(
      'appointments',
      JSON.stringify({ ...appointments, [dateString]: appointment })
    );
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div className="h-full">
      <div id="calender-wrapper" className="h-full">
        {/* ... your existing code */}
        <table id="my-calender" className="w-full h-full border-collapse">
          <thead>
            {/* ... your existing code */}
          </thead>
          <tbody className="bg-transparent">
            <Days currentMonth={currentMonth} onDayClick={handleDayClick} appointments={appointments} />
          </tbody>
        </table>
      </div>
      {showPopup && (
        <AppointmentPopup
          selectedDate={selectedDate}
          currentMonth={currentMonth}
          appointments={appointments}
          onClose={handleClosePopup}
          onSave={handleSaveAppointment}
        />
      )}
    </div>
  );
}

export default Calender;
