import React, { useState } from 'react';

function AppointmentPopup({ selectedDate, onClose, onSave }) {
  const [appointment, setAppointment] = useState('');
  const [time, setTime] = useState('');

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    if (name === 'appointment') {
      setAppointment(value);
    } else if (name === 'time') {
      // Convert the time value to a string in 24-hour format before setting it in state
      const timeString = formatTimeTo24Hour(value);
      setTime(timeString);
    }
  };

  // Function to convert time to 24-hour format
  const formatTimeTo24Hour = (time) => {
    const [hours, minutes] = time.split(':');
    const isPM = hours.includes('PM');

    let hoursInt = parseInt(hours, 10);
    if (isPM && hoursInt < 12) {
      hoursInt += 12;
    } else if (!isPM && hoursInt === 12) {
      hoursInt = 0;
    }

    return `${hoursInt.toString().padStart(2, '0')}:${minutes}`;
  };

  const handleSave = () => {
    onSave(selectedDate, appointment, time); // Pass time to onSave function
    onClose();
  };

  // Save the appointment and time in local storage
  const saveAppointmentAndTimeToLocalStorage = () => {
    const appointmentObj = {
      appointment: appointment,
      time: time,
    };
    localStorage.setItem('appointment', JSON.stringify(appointmentObj));
  };

  return (
    <div className="fixed top-0 left-0 right-0 bottom-0 flex items-center justify-center bg-gray-700 bg-opacity-50">
      <div className="bg-white p-4 rounded-lg shadow-md">
        <h3 className="text-lg font-semibold mb-2">Add Appointment</h3>
        <p className="mb-2">Selected Date: {selectedDate.toDateString()}</p>

        {/* Add input fields for time and appointment */}
        <input
          className="w-full p-2 border border-gray-300 rounded mb-2"
          type="time"
          name="time"
          value={time}
          onChange={handleInputChange}
        />
        <textarea
          className="w-full h-32 p-2 border border-gray-300 rounded mb-2"
          placeholder="Enter your appointment..."
          name="appointment"
          value={appointment}
          onChange={handleInputChange}
        ></textarea>

        <div className="mt-2 flex justify-end">
          <button
            className="px-4 py-2 mr-2 bg-gray-400 text-white rounded"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="px-4 py-2 bg-green-500 text-white rounded"
            onClick={handleSave}
          >
            Save
          </button>
        </div>
      </div>
    </div>
  );
}

export default AppointmentPopup;
