import React, { useState, useEffect } from 'react';

function UpcomingEvents() {
  const [appointments, setAppointments] = useState(
    JSON.parse(localStorage.getItem('appointments')) || {}
  );
  const [upcomingEvents, setUpcomingEvents] = useState([]);

  useEffect(() => {
    const currentDate = getCurrentDate();
    const nextWeekDate = getNextWeekDate();

    const filteredEvents = Object.entries(appointments).filter(([dateString, _]) => {
      const appointmentDate = new Date(dateString);
      return appointmentDate >= currentDate && appointmentDate < nextWeekDate;
    });

    setUpcomingEvents(filteredEvents);
  }, [appointments]);

  const getCurrentDate = () => {
    const currentDate = new Date();
    currentDate.setHours(0, 0, 0, 0); // Set hours, minutes, seconds, and milliseconds to 0
    return currentDate;
  };

  const getNextWeekDate = () => {
    const nextWeekDate = new Date();
    nextWeekDate.setDate(nextWeekDate.getDate() + 7);
    nextWeekDate.setHours(0, 0, 0, 0);
    return nextWeekDate;
  };

  return (
    <div className="upcoming-events">
      <h2>Upcoming Events</h2>
      <ul>
        {upcomingEvents.map(([dateString, appointment], index) => (
          <li key={index}>
            {new Date(dateString).toDateString()}: {appointment}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UpcomingEvents;
