import React, { useState } from 'react';
import Days from './Days';

function Months({ currentMonth, setCurrentMonth }) {
  const handleNextMonth = () => {
    const nextMonth = new Date(
      currentMonth.getFullYear(),
      currentMonth.getMonth() + 1,
      currentMonth.getDate()
    );
    setCurrentMonth(nextMonth);
  };

  const handlePreviousMonth = () => {
    const previousMonth = new Date(
      currentMonth.getFullYear(),
      currentMonth.getMonth() - 1,
      currentMonth.getDate()
    );
    setCurrentMonth(previousMonth);
  };

  return (
    <div>
      <h1 className="text-3xl font-bold hover:text-gray-700 text-center p-2">Calendar</h1>
      <div>
      <button onClick={handlePreviousMonth} className="inline cursor-pointer hover:cursor-pointer">&lt;</button>
      <div> currentMonth </div>
      <button onClick={handleNextMonth} className="inline cursor-pointer hover:cursor-pointer">&gt;</button>
      </div>
      <Days currentMonth={currentMonth} />
    </div>
  );
}



export default Months;
