import React, { useEffect, useState } from 'react';

function Days() {
  const [todayDate, setTodayDate] = useState('');

  useEffect(() => {
    const date = new Date();
    const formattedDate = date.toLocaleDateString();
    setTodayDate(formattedDate);
    fillCalendar(date);
  }, []);

  const fillCalendar = (today_date) => {
    const firstDay = new Date(todayDate.getFullYear(), todayDate.getMonth(), 1);
    const lastDay = new Date(todayDate.getFullYear(), todayDate.getMonth() + 1, 0);

    let check_day = firstDay.getDay();
    let first_weekday = check_day === 0 ? 6 : check_day - 1;
    let counter = 1;
    let counter2 = 1;

    const rows = [];
    let row = [];

    while (counter <= lastDay.getDate() || counter2 <= 50) {
      for (let i = 0; i < 7; i++) {
        counter2 += 1;
        if (first_weekday !== i && counter === 1) {
          row.push(<td key={`empty-${i}`}></td>);
        } else if (first_weekday === i && counter === 1) {
          row.push(<td key={`day-${counter}`} className="bg-transparent bg-blue-100 text-center">{counter}</td>);
          counter += 1;
        } else if (counter <= lastDay.getDate()) {
          row.push(<td key={`day-${counter}`} className="bg-transparent bg-red-400 text-center">{counter}</td>);
          counter += 1;
        }

        if (i === 6) {
          rows.push(<tr key={`row-${counter2}`}>{row}</tr>);
          row = [];
        }
      }
    }

    return <tbody>{rows}</tbody>;
  };
}

  export default Days;
